import React, { useState } from "react";


export const EditTodo = ({editTodo,task}) =>{

    const [newTask, setNewtask] = useState("")

    const handleSubmit = e => {
        e.preventDefault();
        editTodo(newTask,task.index);
        setNewtask("");
    }

    const inputtask = (event) =>{
        setNewtask(event.target.value);
    }
   

    return(
        Todo.isEditing ? (
            <EditTodo />
        ):(
            <Todo />
        )
        <
        <div className="form">
            <input 
                type="text"
                placeholder="update Task"
                value={newTask}
                className="input"
                onChange={inputtask}>
            </input>
            <button
                className="add-button"
                type="button"
                onClick={handleSubmit}
                 >
                    Update
            </button>
       </div>
    )
}
