import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPenToSquare } from '@fortawesome/free-solid-svg-icons'
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { EditTodo } from "./EditTodo";

const Todo = () =>
{
    const[task,setTasks] = useState([]);
    const[newTask,setNewtask] = useState("");

    const inputtask = (event) =>{
        setNewtask(event.target.value);
    }

    const addNewtask= ()=>{
        if(!task.includes(newTask) && newTask !== ""){
            setTasks(t => [...t,newTask]);
            setNewtask("");
        }
        else if(task.includes(newTask))
        {
            window.alert("Task already exist");
        }
    }

    const deleteTask = (index) =>{
        const updatedTask = task.filter((_,i) => i !== index);
        setTasks(updatedTask);
    }


    const editTask = (index) => {
            setTasks(task.map(task => task.index === index ?{...task, isEditing: !task.isEditing} : task))
    }

    return(
        <>
         <h1>To-Do List</h1>
        <div className="todo-view">
           <div className="form">
            <input 
                type="text"
                placeholder="Enter a task"
                value={newTask}
                className="input"
                onChange={inputtask}>
            </input>
            <button
                className="add-button"
                type="button"
                onClick={addNewtask}
                 >
                    Add
            </button>
       
        <ol>
            isEditing ?(
                <EditTodo />
            ) :(
            {task.map((task,index) =>
                <li key={index}>
                    <span contentEditable="false" className="task-view">{task}</span>
                    <button 
                    className="edit-button"
                    onClick={() => editTask(index)}
                    >
                        <FontAwesomeIcon icon={faPenToSquare} />
                    </button>
                    <button
                    className="delete-button"
                    onClick={()=> deleteTask(index)}
                     >
                        <FontAwesomeIcon icon={faTrash} />
                    </button>
                </li>
            
        )}
        </ol>
        </div>
        </div>
        </>
    );
}

export default Todo;
